int findMax(int arr[], int n) {
  
      // Assume the first element is the largest
    int max = arr[0];
    for (int i = 1; i < n; i++) {
      
          // Update max if arr[i] is greater
        if (arr[i] > max) {
            max = arr[i];
        }
