        printf("Index 2: ");
        scanf("%d", &index2);

        if (index1 >= 0 && index1 < N && index2 >= 0 && index2 < N) {
            break;
        } else {
            printf("Error: Indices must be within the range of the array elements. Please try again.\n", N);
        }
    }

    // 5) Use pointer-based approach to compute the sum
    sum = *(arr + index1) + *(arr + index2);

    // 6) Print the sum
