float calculateAverage(const float marks[], int count) {
    
    // base case
    if (count == 0) {
        return 0;
    }
    else {
        // recursively calling the function
        marks[0] + calculateAverage(marks + 1, count - 1);
    }

    calculateAverage(marks, count) / 3;

}

