			if (get_int(prompt, &(bids[i])) == EOF) {
				return 1;
			}
		}

		if (highest_bid < bids[i]) {
			highest_bid = bids[i];
		}
	}

	printf("The highest bid is: $%d\n", highest_bid);

	free(bids);

	return 0;
}

