#define MAX_SIZE 10
//function to transpose a matrix
void transpose(int arr[][MAX_SIZE], int r, int c) {
    int trans[MAX_SIZE][MAX_SIZE];
    for (int i = 0; i < r; ++i)
        for (int j = 0; j < c; ++j) {
            trans[j][i] = arr[i][j];
        }
    
    for (int i = 0; i < c; ++i)
        for (int j = 0; j < r; ++j) {
            printf("%d ", trans[i][j]);
            if (j == r - 1) {
                printf("\n");
            }
        }
}
//function to add 2 matrices
void add(int a[][MAX_SIZE], int b[][MAX_SIZE], int r, int c) {
    int sum[MAX_SIZE][MAX_SIZE];
    for (int i = 0; i < r; ++i)
        for (int j = 0; j < c; ++j) {
            sum[i][j] = a[i][j] + b[i][j];
        }
   
    for (int i = 0; i < r; ++i)
        for (int j = 0; j < c; ++j) {
            printf("%d ", sum[i][j]);
            if (j == c - 1) {
                printf("\n");
            }
        }
}
//function to subtract matrices
void sub(int a[][MAX_SIZE], int b[][MAX_SIZE], int r, int c) {
    int sum[MAX_SIZE][MAX_SIZE];
    for (int i = 0; i < r; ++i)
        for (int j = 0; j < c; ++j) {
            sum[i][j] = a[i][j] - b[i][j];
        }
   
    for (int i = 0; i < r; ++i)
        for (int j = 0; j < c; ++j) {
            printf("%d ", sum[i][j]);
            if (j == c - 1) {
                printf("\n");
            }
        }
}

