// Functions 
int add_matrix(int a[MAX_ROW][MAX_COL], int b[MAX_ROW][MAX_COL], int row, int col, int result[MAX_ROW][MAX_COL]){
    // Matrix A + Matrix B
    for(int i=0; i < row; i++){
        for (int j=0; j < col; j++){
            result[i][j] = a[i][j] + b[i][j];
        }
    }
    // print results
    puts("Result of Matrix A + Matrix B:");
    for(int i=0; i < row; i++){
        for (int j=0; j < col; j++){
            printf("%d ", result[i][j]);
        }
        printf("\n");
    }
    return 0;
}

int subtract_matrix(int a[MAX_ROW][MAX_COL], int b[MAX_ROW][MAX_COL], int row, int col, int result[MAX_ROW][MAX_COL]){
    // Matrix A - Matrix B
    for(int i=0; i < row; i++){
        for (int j=0; j < col; j++){
            result[i][j] = a[i][j] - b[i][j];
        }
    }
    // print results
    puts("Result of Matrix A - Matrix B:");
    for(int i=0; i < row; i++){
        for (int j=0; j < col; j++){
            printf("%d ", result[i][j]);
        }
        printf("\n");
    }
    return 0;
}

// swap values of row & cols
int transpose_matrix(int matrix[MAX_ROW][MAX_COL], int row, int col, int result[MAX_ROW][MAX_COL]){
    for(int i=0; i < row; i++){
        for (int j=0; j < col; j++){
            result[j][i]=matrix[i][j];
        }
        /*
        exp: 
            [0] [1] [2]
        [0]  1   2   3
        [1]  4   5   6

        when i=0, j=2
        result[2][0] = matrix[0][2] = 3

        result =    [0] [1] [2]
                 [0]    
                 [1]
                 [2] 3    
        
        when i=1, j=0
        result[0][1] = matrix[1][0] = 4
        
        result =    [0] [1] [2]
                 [0]     4
                 [1]
                 [2] 3   
                 .
                 :
        final result =      [0] [1] [2]
                        [0]  1   4
                        [1]  2   5
                        [2]  3   6
        */
    }
    // print transposed matrix
    for(int i=0; i < col; i++){
        for (int j=0; j < row; j++){
            printf("%d ", result[i][j]);
        }
        printf("\n");
    }
    return 0;
}
