    item->category[strcspn(item->category, "\n")] = '\0'; // Remove newline

    printf("Enter Quantity: "); //item quantity
    scanf("%d", &item->quantity);

    printf("Enter Price: "); //item price
    scanf("%f", &item->price);

    item->inStock = item->quantity > 0;
    inventory->itemCount++;

