    while (1) {
        if (promptSize(&rowB, &colB, 'B')==-1) {
            continue;
        }
        break;
    }
    // Initializes matrix B
    int MatrixB[rowB*colB];
    createMatrix(MatrixB, rowB, colB, 'B');

