                        printf("Enter price: ");
                        if (fgets(buffer, sizeof(buffer), stdin) != NULL) {
                            Trim(buffer);
                            size_t posCheck = isPos(buffer);
                            if (posCheck == 0) {
                                newItem->price = atof(buffer);
