// Trim function to remove newline character
void Trim(char *str) {
    size_t length = strlen(str);
    if (length > 0 && str[length - 1] == '\n') {
        str[length - 1] = '\0';
    }
}

// Function to ensure input is only positive number
size_t isPos(char *message) {
    while (*message != '\0') {
        if ((*message < '0' || *message > '9') && *message != '.') {
            return 1;
            break;
        }
        message++;
    }
    return 0;
}
