#include <stdio.h>
#include <string.h>
#include <stdbool.h>

#define MAX_ITEMS 100
#define MAX_NAME_LEN 100
#define MAX_CATEGORY_LEN 50

// Struct to store grocery item details
typedef struct {
    char name[MAX_NAME_LEN];
    char category[MAX_CATEGORY_LEN];
    int quantity;
    float price;
} GroceryItem;

// Struct to store availability status
typedef struct {
    bool inStock;
    bool outOfStock;
} AvailabilityStatus;

// Struct to store the grocery inventory
typedef struct {
    GroceryItem items[MAX_ITEMS];
    AvailabilityStatus status[MAX_ITEMS];
    int count;
} GroceryInventory;

// Function prototypes
void addGroceryItem(GroceryInventory *inventory);
void listGroceryItems(const GroceryInventory *inventory);
void updateItemQuantity(GroceryInventory *inventory);
void removeGroceryItem(GroceryInventory *inventory);

int main() {
    GroceryInventory inventory = { .count = 0 };
    int choice;

    do {
        printf("\nGrocery Inventory Management System\n");
        printf("1. Add Grocery Item\n");
        printf("2. List All Grocery Items\n");
        printf("3. Update Quantity\n");
        printf("4. Remove Grocery Item\n");
        printf("5. Exit\n");
        printf("Enter your choice: ");
        scanf("%d", &choice);
        getchar(); // Consume newline

        switch (choice) {
            case 1:
                addGroceryItem(&inventory);
                break;
            case 2:
                listGroceryItems(&inventory);
                break;
            case 3:
                updateItemQuantity(&inventory);
                break;
            case 4:
                removeGroceryItem(&inventory);
                break;
            case 5:
                printf("Exiting...\n");
                break;
            default:
                printf("Invalid choice. Please try again.\n");
        }
    } while (choice != 5);

    return 0;
}

void addGroceryItem(GroceryInventory *inventory) {
    if (inventory->count >= MAX_ITEMS) {
        printf("Inventory is full. Cannot add more items.\n");
        return;
    }

    GroceryItem *item = &inventory->items[inventory->count];
    AvailabilityStatus *status = &inventory->status[inventory->count];

    printf("Enter item name: ");
    fgets(item->name, MAX_NAME_LEN, stdin);
    item->name[strcspn(item->name, "\n")] = '\0'; // Remove newline

    printf("Enter item category: ");
    fgets(item->category, MAX_CATEGORY_LEN, stdin);
    item->category[strcspn(item->category, "\n")] = '\0'; // Remove newline

    printf("Enter quantity: ");
    scanf("%d", &item->quantity);

    printf("Enter price: ");
    scanf("%f", &item->price);

    getchar(); // Consume newline

    status->inStock = (item->quantity > 0);
    status->outOfStock = !status->inStock;

    inventory->count++;
    printf("Grocery item added successfully!\n");
}

void listGroceryItems(const GroceryInventory *inventory) {
    if (inventory->count == 0) {
        printf("No grocery items in the inventory.\n");
        return;
    }

    for (int i = 0; i < inventory->count; i++) {
        const GroceryItem *item = &inventory->items[i];
        const AvailabilityStatus *status = &inventory->status[i];

        printf("Name: %s\n", item->name);
        printf("Category: %s\n", item->category);
        printf("Quantity: %d\n", item->quantity);
        printf("Price: %.2f\n", item->price);
        printf("Status: %s\n", status->inStock ? "In Stock" : "Out of Stock");
    }
}

void updateItemQuantity(GroceryInventory *inventory) {
    if (inventory->count == 0) {
        printf("No items in the inventory to update.\n");
        return;
    }
    char name[MAX_NAME_LEN];
    printf("Enter the name of the grocery item to update quantity: ");
    fgets(name, MAX_NAME_LEN, stdin);
    name[strcspn(name, "\n")] = '\0';   // Remove newline

    for (int i = 0; i < inventory->count; i++) {
        GroceryItem *item = &inventory->items[i];
        AvailabilityStatus *status = &inventory->status[i];

        if (strcspn(item->name, name) == 0){
            printf("Enter new quantity for %s: ", item->name);
            scanf("%d", &item->quantity);

            status->inStock = (item->quantity > 0);
            status->outOfStock = !status->inStock;

            printf("Quantity updated successfully!\n");
            return;
        }
    }
    printf("Item not found in the inventory.\n");
}

void removeGroceryItem(GroceryInventory *inventory) {
    if (inventory->count == 0) {
        printf("No items in the inventory to remove.\n");
        return;
    }

    char name[MAX_NAME_LEN];
    printf("Enter the name of the grocery item to remove: ");
    fgets(name, MAX_NAME_LEN, stdin);
    name[strcspn(name, "\n")] = '\0';

    for (int i = 0; i < inventory->count; i++) {
        if (strcspn(inventory->items[i].name, name) == 0) {
            printf("Grocery item '%s' removed successfully!\n", inventory->items[i].name);
            for (int j = i; j < inventory->count - 1; j++) {
                inventory->items[j] = inventory->items[j + 1];
                inventory->status[j] = inventory->status[j + 1];
            }

            inventory->count--;
            return;
        }
    }

    printf("Item not found in the inventory.\n");

}

/* --- L3Q3.c --- */
#include <stdio.h>
#include <stdlib.h>
#include <string.h>

// Structure for Student
typedef struct Student {
    char name[100];
    int id;
    float grade;
    struct Student* next;   // Pointer to the next student in the list
} Student;

Student* head = NULL;

// Function to create a new student node
Student* createStudent(const char* name, int id, float grade) {
    // Allocate memory for the new student node
    Student* newStudent = (Student*)malloc(sizeof(Student));
    if (newStudent == NULL) {
        printf("Memory allocation failed!\n");
        exit(1);
    }

    // Copy the student details into the ode
    strcpy(newStudent->name, name);
    newStudent->id = id;
    newStudent->grade = grade;
    newStudent->next = NULL;    // Initialize the next pointer to NULL

    return newStudent;  // Return the newly created student node
}

// Function to insert a new student record
void insertStudent(const char* name, int id, float grade) {
    // Create a new student node
    Student* newStudent = createStudent(name, id, grade);

    // Insert the node at the beginnning of the list
    newStudent->next = head;
    head = newStudent;

    printf("Student record inserted successfully.\n");
}

// Function to display all student ecords
void displayStudents() {
    // Check is the list is empty
    if (head == NULL) {
        printf("No student records found.\n");
        return;
    }

    // Tranverse the list and print each student record
    Student* current = head;
    printf("\nStudent Records:\n");
    while (current != NULL) {
        printf("Name: %s, ID: %d, Grade: %.2f\n", current->name, current->id, current->grade);
        current = current->next;
    }
}

// Function to search for a student by ID
Student* searchStudentByID(int id) {
    // Trancerse the list to find the student with the given ID
    Student* current = head;
    while (current != NULL) {
        if (current->id == id) {
            return current; // Return the student ode if found
        }
        current = current->next;
    }
    return NULL;    // Return NULL if the student node if found
}

// Function to delete the entire list
void deleteList() {
    Student* current = head;
    Student* next;

    // Free each node in the list
    while (current != NULL) {
        next = current->next;
        free(current);
        current = next;
    }

    head = NULL;    //Reset the head pointer to NULL
    printf("All student records deleted.\n");
}

// Utility function to free memory (alias for deleteList)
void freeMemory() {
    deleteList();
}

// Sort function to sort the student records by grade using based on Bubble sort
void sortStudents() {
    // Check if the list is empty or has only one element
    if (head == NULL || head-> next == NULL) {
        return;
    }

    int swapped;
    Student* ptr1;
    Student* lptr = NULL;

    // Bubble Sort implementation
    do {
        swapped = 0;
        ptr1 = head;

        while (ptr1->next != lptr) {
            if (ptr1->grade > ptr1->next->grade) {
                // Swap the student details
                char tempName[100];
                int tempID;
                float tempGrade;

                strcpy(tempName, ptr1->name);
                tempID = ptr1->id;
                tempGrade = ptr1->grade;

                strcpy(ptr1->name, ptr1->next->name);
                ptr1->id = ptr1->next->id;
                ptr1->grade = ptr1->next->grade;

                strcpy(ptr1->next->name, tempName);
                ptr1->next->id = tempID;
                ptr1->next->grade = tempGrade;

                swapped = 1;
            }
            ptr1 = ptr1->next;
        }
        lptr = ptr1;
    } while (swapped);

    printf("Student records sorted by grade.\n");
}

// Main function to drive the program
int main() {

    int choice, id;
    char name[100];
    float grade;

    do {
        printf("\n1. Insert Student Record\n");
        printf("2. Display Student Records\n");
        printf("3. Sort Records\n");
        printf("4. Search Record by ID\n");
        printf("5. Delete List\n");
        printf("6. Exit\n");
        printf("Enter your choice: ");
        scanf("%d", &choice);
        getchar();  // To consume the newline character

        switch (choice) {
            case 1:
                printf("Enter name: ");
                fgets(name, sizeof(name), stdin);
                strtok(name, "\n"); // Remove newline character
                printf("Enter ID: ");
                scanf("%d", &id);
                printf("Enter grade: ");
                scanf("%f", &grade);
                insertStudent(name, id, grade);
                break;

            case 2:
                displayStudents();
                break;

            case 3:
                sortStudents();
                printf("Records sorted by grade using Bubble Sort.\n");
                break;

            case 4:
            printf("Enter ID to search: ");
            scanf("%d", &id);
            Student* found = searchStudentByID(id);
            if (found) {
                printf("Student Found: %s, ID: %d, Grade: %.2f\n", found->name, found->id, found->grade);
            } else {
                printf("Student with ID %d, not found.\n", id);
            }
            break;

            case 5:
                deleteList();
                printf("List deleted.\n");
                break;

            case 6:
                freeMemory();
                printf("Exiting...\n");
                break;

            default:
                printf("Invalid choice! Please try again.\n");
        }
    } while (choice != 6);

    return 0;
}

