    int T_A[MAX_SIZE][MAX_SIZE], T_B[MAX_SIZE][MAX_SIZE];                      // Declare matrices to store transposed matrices
    int rowsA, colsA, rowsB, colsB;                       // Variables to store dimensions of matrices A and B
