    for (int i = 0; i < rowsA; i++) {            // Iterate through each row
        for (int j = 0; j < colsA; j++) {        // Iterate through each column.
            printf("Element [%d][%d]: ", i+1, j+1);     // Prompt user for input at position (i+1, j+1)
