    char name[100];
    printf("Enter item name to remove: ");
    scanf(" %[^\n]", name);

    for (int i = 0; i < inv->itemCount; i++) {
        if (strcmp(inv->items[i].name, name) == 0) {
            for (int j = i; j < inv->itemCount - 1; j++) {
                inv->items[j] = inv->items[j + 1]; // Shift items
            }
            inv->itemCount--;
            return;
        }
    }
    printf("Item not found.\n");
}

