                i->grade = j->grade; 
                j->grade = tempGrade; 

                int tempId = i->id; 
                i->id = j->id; 
                j->id = tempId; 

                char tempName[100]; 
                strcpy(tempName, i->name); 
                strcpy(i->name, j->name); 
                strcpy(j->name, tempName); 
            } 
        } 
    } 
}

