}

// Bubble sort implementation
void sortStudents() {
    if (head == NULL || head->next == NULL) {
        return;
    }
    
    int swapped;
    Student* current;
    Student* last = NULL;
    
    do {
        swapped = 0;
        current = head;
        
        while (current->next != last) {
            if (current->grade > current->next->grade) {
                // Swap data
                char tempName[MAX_NAME];
                strcpy(tempName, current->name);
                int tempId = current->id;
