    removeName[strcspn(removeName, "\n")] = '\0'; // Remove newline

    //loop to find item
    for (int i = 0; i < (*inventory).total; i++) {
        if (strcmp((*inventory).items[i].name, removeName) == 0) {
            //removing item 
            for (int j = i; j < (*inventory).total - 1; j++) {
