    groceryInventory inventory = {0};
    int choice;

    do {
        menu();
        printf("Enter your choice: ");
        scanf("%d", &choice);

        switch (choice) { // use switch-case
            case 1:
                add(&inventory);
                break;
            case 2:
                list(&inventory);
                break;
