#include <stdio.h>
#include <stdlib.h>
#include <string.h>

// Structure for Student
typedef struct Student {
    char name[100];
    int id;
    float grade;
    struct Student* next;
} Student;

Student* head = NULL;  

// Function to create a new student node
Student* createStudent(const char* name, int id, float grade) {
    Student* newStudent = (Student*)malloc(sizeof(Student));
    if (newStudent == NULL) {
        printf("Memory allocation failed!\n");
        return NULL;
    }
    strcpy(newStudent->name, name);
    newStudent->id = id;
    newStudent->grade = grade;
    newStudent->next = NULL;
    return newStudent;
}

// Function to insert a new student record
void insertStudent(const char* name, int id, float grade) {
    Student* newStudent = createStudent(name, id, grade);
    if (newStudent == NULL) return;

    newStudent->next = head;
    head = newStudent;
}

// Function to display all student records
void displayStudents() {
    if (head == NULL) {
        printf("No student records to display.\n");
        return;
    }

    Student* temp = head;
    
    while (temp != NULL) {
        printf("Name: %s, ID: %d, Grade: %.2f\n", temp->name, temp->id, temp->grade);
        temp = temp->next;
    }
}

// Function to search for a student by ID
Student* searchStudentByID(int id) {
    Student* temp = head;
    while (temp != NULL) {
        if (temp->id == id) {
            return temp;
        }
        temp = temp->next;
    }
    return NULL;
}

// Function to delete the entire list
void deleteList() {
    Student* current = head;
    Student* next;
    while (current != NULL) {
        next = current->next;
        free(current);
        current = next;
    }
    head = NULL;
}

// Free all memory used by the program
void freeMemory() {
    deleteList();

}

// Swap function to swap two student nodes
void swap(Student* a, Student* b) {
    char tempName[100];
    int tempID;
    float tempGrade;

    // Swap the data of two students
    strcpy(tempName, a->name);
    tempID = a->id;
    tempGrade = a->grade;

    strcpy(a->name, b->name);
    a->id = b->id;
    a->grade = b->grade;

    strcpy(b->name, tempName);
    b->id = tempID;
    b->grade = tempGrade;
}

// Function to sort the student records by grade using Bubble Sort
void sortStudents() {
    if (head == NULL || head->next == NULL) {
        return;  // No need to sort if the list has 0 or 1 element
    }

    int swapped;
    Student *ptr1;
    Student *ptr2 = NULL;

    // Sorting the list by grades using Bubble Sort
    do {
        swapped = 0;
        ptr1 = head;

        while (ptr1->next != ptr2) {
            if (ptr1->grade > ptr1->next->grade) {
                swap(ptr1, ptr1->next);
                swapped = 1;
            }
            ptr1 = ptr1->next;
        }
        ptr2 = ptr1;
    } while (swapped);
    printf("Records sorted by grade using Bubble Sort.\n");
}

