    float price;
} Item;

typedef struct {
    bool inStock;
    bool outOfStock;
} Status;

Item itemList[MAX_ITEMS];
Status statusList[MAX_ITEMS]; 
int itemCount = 0; // to track the number of items in list

// Declare prototypes function
void addItem();
void listItem();
void updateStatus(int count);
