    item->category[strcspn(item->category, "\n")] = 0; // Remove newline character

    printf("Enter Quantity: ");
    while (scanf("%d", &item->quantity) != 1) {
        printf("Invalid input. Please enter an integer.\n");
        while (getchar() != '\n'); // Clear input buffer
        printf("Enter Quantity: ");
