#include <stdio.h>
#include <stdlib.h>
#include <string.h>

// Structure for Student
typedef struct Student {
    char name[100];
    int id;
    float grade;
    struct Student* next;
} Student;

Student* head = NULL;

// Function to create a new student node
Student* createStudent(const char* name, int id, float grade) {
    // Allocate memory for new student
    Student* newStudent = (Student*)malloc(sizeof(Student));
    
    // Check if memory allocation was successful
    if (newStudent == NULL) {
        printf("Memory allocation failed!\n");
        return NULL;
    }
    
    // Copy name, set id and grade
    strcpy(newStudent->name, name);
    newStudent->id = id;
    newStudent->grade = grade;
    newStudent->next = NULL;
    
    return newStudent;
}

// Function to insert a new student record
void insertStudent(const char* name, int id, float grade) {
    // Create a new student node
    Student* newStudent = createStudent(name, id, grade);
    
    // If memory allocation failed
    if (newStudent == NULL) return;
    
    // If list is empty or new student should be at the beginning
    if (head == NULL || head->id > newStudent->id) {
        newStudent->next = head;
        head = newStudent;
        printf("Student record inserted successfully!\n");
        return;
    }
    
    // Find the correct position to insert
    Student* current = head;
    while (current->next != NULL && current->next->id < newStudent->id) {
        current = current->next;
    }
    
    // Insert the new student
    newStudent->next = current->next;
    current->next = newStudent;
    
    printf("Student record inserted successfully!\n");
}

// Function to display all student records
void displayStudents() {
    if (head == NULL) {
        printf("No student records found.\n");
        return;
    }
    
    printf("Student Records:\n");
    printf("%-30s %-10s %-10s\n", "Name", "ID", "Grade");
        
    Student* current = head;
    while (current != NULL) {
        printf("%-30s %-10d %-10.2f\n", 
               current->name, current->id, current->grade);
        current = current->next;
    }
}

// Function to search for a student by ID
Student* searchStudentByID(int id) {
    Student* current = head;
    while (current != NULL) {
        if (current->id == id) {
            return current;
        }
        current = current->next;
    }
    return NULL;
}

// Function to delete the entire list
void deleteList() {
    Student* current = head;
    Student* next;
    
    while (current != NULL) {
        next = current->next;
        free(current);
        current = next;
    }
    
    head = NULL;
    printf("Entire student list has been deleted.\n");
}

// Function to free memory
void freeMemory() {
    deleteList();
}

// Function to sort students by grade
void sortStudents() {
    // If list is empty or has only one element, no need to sort
    if (head == NULL || head->next == NULL) {
        return;
    }
    
    int swapped;
    Student* ptr1;
    Student* lptr = NULL;
    
    do {
        swapped = 0;
        ptr1 = head;
        
        while (ptr1->next != lptr) {
            if (ptr1->grade > ptr1->next->grade) {
                // Swap data
                Student temp;
                strcpy(temp.name, ptr1->name);
                temp.id = ptr1->id;
                temp.grade = ptr1->grade;
                
                strcpy(ptr1->name, ptr1->next->name);
                ptr1->id = ptr1->next->id;
                ptr1->grade = ptr1->next->grade;
                
                strcpy(ptr1->next->name, temp.name);
                ptr1->next->id = temp.id;
                ptr1->next->grade = temp.grade;
                
                swapped = 1;
            }
            ptr1 = ptr1->next;
        }
        lptr = ptr1;
    } while (swapped);
    
    printf("Student records sorted by grade in ascending order.\n");
}

// Main function to drive the program
int main() {
    int choice, id;
    char name[100];
    float grade;
    
    do {
        printf("\n1. Insert Student Record\n");
        printf("2. Display Student Records\n");
        printf("3. Sort Records (by grade)\n");
        printf("4. Search Record by ID\n");
        printf("5. Delete List\n");
        printf("6. Exit\n");
        printf("Enter your choice: ");
        scanf("%d", &choice);
        getchar(); // To consume the newline character
        
        switch (choice) {
            case 1:
                printf("Enter Name: ");
                fgets(name, sizeof(name), stdin);
                strtok(name, "\n");
                printf("Enter ID: ");
                scanf("%d", &id);
                printf("Enter Grade: ");
                scanf("%f", &grade);
                insertStudent(name, id, grade);
                break;
            case 2:
                displayStudents();
                break;
            case 3:
                sortStudents();
                break;
            case 4:
                printf("Enter ID to search: ");
                scanf("%d", &id);
                Student* found = searchStudentByID(id);
                if (found) {
                    printf("Found: Name: %s, ID: %d, Grade: %.2f\n", 
                           found->name, found->id, found->grade);
                } else {
                    printf("Student not found.\n");
                }
                break;
            case 5:
                deleteList();
                break;
            case 6:
                freeMemory();
                printf("Exiting...\n");
                break;
            default:
                printf("Invalid choice! Please try again.\n");
        }
    } while (choice != 6);
    
    return 0;
}

