#include <stdio.h>
#include <stdlib.h>
#include <string.h>

// Structure for Student
typedef struct Student {
    char name[100];
    int id;
    float grade;
    struct Student* next;
} Student;

Student* head = NULL;

// Function to create a new student node
Student* createStudent(const char* name, int id, float grade) {
    //Allocate memory for a new Student structure
    Student* newStudent = (Student*)malloc(sizeof(Student));
    //Check if memory allocation was successful
    if (newStudent == NULL) {
        perror("Memory allocation failed");
        exit(EXIT_FAILURE); 
    }
    strcpy(newStudent->name, name);
    newStudent->id = id;
    newStudent->grade = grade;
    //Initialize the 'next' pointer of the new student to NULL
    newStudent->next = NULL;
    //Return a pointer to the newly created student node
    return newStudent;
}

// Function to insert a new student record
void insertStudent(const char* name, int id, float grade) {
    Student* newStudent = createStudent(name, id, grade);
    //Check if the linked list is empty
    if (head == NULL) {
        head = newStudent;
    } else {
        Student* temp = head;
        //Traverse the list until 'temp' reaches the last node
        while (temp->next != NULL) {
            temp = temp->next;
        }
        //After the loop, 'temp' points to the last node
        temp->next = newStudent;
    }
}

// Function to display all student records
void displayStudents() {
    if (head == NULL) {
        printf("No student records to display.\n");
        return;
    }
    Student* temp = head;
    //print the student information of the node that the pointer is currently referencing within the loop
    while (temp != NULL) {
        printf("Name: %s, ID: %d, Grade: %.2f\n", temp->name, temp->id, temp->grade);
        temp = temp->next;
    }
}

// Function to search for a student by ID
Student* searchStudentByID(int id) {
    //This pointer will be used to traverse the list to find the specified student ID
    Student* temp = head;
    //Use while loop to check if the 'id' of the current node pointed to by 'temp' matches the target 'id'
    while (temp != NULL) {
        if (temp->id == id) {
            return temp;
        }
        temp = temp->next;
    }
    return NULL;
}

// Function to delete the entire list
void deleteList() {
    Student* current = head;
    Student* next;

    while (current != NULL) {
        next = current->next;
        free(current);
        current = next;
    }
    head = NULL;
}

void freeMemory() {
    deleteList();
}

// Sort function to sort the student records by grade based on an algorithm of your choice
void sortStudents() {
    printf("Records sorted by grade using Bubble Sort.\n");
    // In cases where the data size is smallbubbling sort is simple to implement and easy to understand.
    if (head == NULL || head->next == NULL) {
        return; 
    }

    int swapped;
    Student* ptr1; 
    Student* lptr = NULL; //Pointer to mark the beginning of the sorted portion

    do {
        swapped = 0; //Reset the swapped flag before each pass
        ptr1 = head;

        //Compare adjacent elements
        while (ptr1->next != lptr) {
            if (ptr1->grade > ptr1->next->grade) {
                //Swap data (name, ID, and grade)
                char tempName[100];
                int tempId;
                float tempGrade;

                strcpy(tempName, ptr1->name);
                tempId = ptr1->id;
                tempGrade = ptr1->grade;

                strcpy(ptr1->name, ptr1->next->name);
                ptr1->id = ptr1->next->id;
                ptr1->grade = ptr1->next->grade;

                strcpy(ptr1->next->name, tempName);
                ptr1->next->id = tempId;
                ptr1->next->grade = tempGrade;

                swapped = 1; //A swap occurred, set the swapped flag
            }
            ptr1 = ptr1->next; //Move to the next node
        }
        lptr = ptr1; //Update the beginning of the sorted portion
    } while (swapped);
}

// Main function to drive the program 
int main() { 

    int choice, id; 
    char name[100]; 
    float grade; 

    do { 
        printf("\n1. Insert Student Record\n"); 
        printf("2. Display Student Records\n"); 
        printf("3. Sort Records\n"); 
        printf("4. Search Record by ID\n"); 
        printf("5. Delete List\n"); 
        printf("6. Exit\n"); 
        printf("Enter your choice: "); 
        scanf("%d", &choice); 
        getchar(); // To consume the newline character 

        switch (choice) { 
            case 1: 
                printf("Enter Name: "); 
                fgets(name, sizeof(name), stdin); 
                strtok(name, "\n"); 
                printf("Enter ID: "); 
                scanf("%d", &id); 
                printf("Enter Grade: "); 
                scanf("%f", &grade); 
                insertStudent(name, id, grade); 
                break; 
            case 2: 
                displayStudents(); 
                break; 
            case 3: 
     sortStudents();                 
                break; 
            case 4: 
                printf("Enter ID to search: "); 
                scanf("%d", &id); 
                Student* found = searchStudentByID(id); 
                if (found) { 
                    printf("Found: Name: %s, ID: %d, Grade: %.2f\n", 
found->name, found->id, found->grade); 
                } else { 
                    printf("Student not found.\n"); 
                } 
                break; 
            case 5: 
                deleteList(); 
                printf("List deleted.\n"); 
                break; 
            case 6: 
                freeMemory(); 
                printf("Exiting...\n"); 
                break; 
            default: 
                printf("Invalid choice! Please try again.\n"); 
        } 
    } while (choice != 6); 

    return 0; 
}

