        case 4:
            tanspose_matrix(matrixB, result, rowB, colB);
            printf("Transpose of Matrix B:\n");
            display_result(result, colB, rowB);
            break;
        case 5:
            printf("Exiting...\n");
            return 0;
        default:
            printf("Error: invalid choice.\n");
            break;
        }
    }
}

