#include <stdio.h>
#include <stdlib.h>
#include <string.h>

// Structure for Student
typedef struct Student {
    char name[100];
    int id;
    float grade;
    struct Student* next;
} Student;

Student* head = NULL;

// Function to create a new student node
Student* createStudent(const char* name, int id, float grade) {

    Student* newStudent = (Student*)malloc(sizeof(Student));     // Allocate memory for a new student node using malloc
    
    // memory error handling
    if (newStudent == NULL) {
        printf("Memory allocation failed!\n");
        return NULL;
    }
    
    
    strcpy(newStudent->name, name); // Copy name, set ID and grade
    newStudent->id = id;
    newStudent->grade = grade;
    newStudent->next = NULL;//initiliase null pointer to null
    
    return newStudent;
}

// Function to insert a new student record
void insertStudent(const char* name, int id, float grade) {
   
    Student* newStudent = createStudent(name, id, grade); //calling createStudent
    
    if (newStudent == NULL) return;//malloc handling
    
    
    if (head == NULL) { //if list empty
        head = newStudent; //make node head
        printf("Student record inserted successfully.\n");
        return;
    }
    
    
    Student* current = head;//make as last node
    while (current->next != NULL) {
        current = current->next;
    }
    current->next = newStudent;
    
    printf("Student record inserted successfully.\n");
}

// Function to display all student records
void displayStudents() {
    
    if (head == NULL) { //check if list empty
        printf("No student records found.\n");
        return;
    }
    
    Student* current = head; //point at the head of the list 
    printf("\nStudent Records:\n");
    while (current != NULL) { //loop through each node until it point to null (end)
        printf("Name: %s, ID: %d, Grade: %.2f\n", current->name, current->id, current->grade);
        current = current->next; //update current pointer to next 
    }
}

// Function to search for a student by ID
Student* searchStudentByID(int id) {
    // Traverse the list to find matching ID
    Student* current = head;
    while (current != NULL) { //loop through pointer
        if (current->id == id) {
            return current; //return node if match
        }
        current = current->next;
    }
    
    
    return NULL;//return null if no match
}

// Function to delete the entire list
void deleteList() {
    Student* current = head;
    Student* next;
    
    while (current != NULL) { //loop through list to free
        next = current->next; 
        free(current); //free current node
        current = next;
    }
    
    head = NULL;
}

// Function to free memory when exiting
void freeMemory() {
    deleteList();
}

// Sort function to sort the student records by grade
void sortStudents() {
    // If list is empty or has only one element, no sorting needed
    if (head == NULL || head->next == NULL) {//check list empty or has only one node
        printf("Not enough node to be sorted\n");
        return;
    }
    
    int swapped;
    Student* current;
    Student* last = NULL;
    
    do { //loop until swapped != 0
        swapped = 0;
        current = head;
        
        while (current->next != last) {
            if (current->grade > current->next->grade) {//swapping if current node bigger than node after
                //bubble sort implementation logic
                Student temp;
                strcpy(temp.name, current->name);
                temp.id = current->id;
                temp.grade = current->grade;
                
                strcpy(current->name, current->next->name);
                current->id = current->next->id;
                current->grade = current->next->grade;
                
                strcpy(current->next->name, temp.name);
                current->next->id = temp.id;
                current->next->grade = temp.grade;
                
                swapped = 1;
            }
            current = current->next;//set current to next node
        }
        last = current;
    }while(swapped) ;
    
    printf("Student records sorted by grade.\n");
}

