    while (current != NULL) {
        if (current->id == id) {
            return current;
        }
        current = current->next;
    }
    return NULL;
}

// Delete the entire linked list
void deleteList() {
    Student* current = head;
    Student* nextNode;
    while (current != NULL) {
        nextNode = current->next;
        free(current);
        current = nextNode;
    }
    head = NULL;
    printf("All student records have been deleted.\n");
}

// release memory
void freeMemory() {
    deleteList();
}

// Sort student records by achievement using bubble sort.
