    // Verify that matrix dimensions are valid
    if (rowsA > MAX_SIZE || colsA > MAX_SIZE || rowsB > MAX_SIZE || colsB > MAX_SIZE || rowsA <= 0 || colsA <= 0 || rowsB <= 0 || colsB <= 0) {
        printf("Invalid matrix dimensions.\n");
