                insertStudent(name, id, grade);
                break;
            case 2:  // Display all student records
                displayStudents();
                break;
            case 3:  // Sort student recors
                sortStudents();
                break;
            case 4:  // Search for a student by ID
                printf("Enter ID to search: ");
