void addItem() {
    if (itemCount >= MAX_ITEMS) {
        printf("Inventory is full. Cannot add more items.\n");
        return;
    }

    GroceryItem item;

    printf("Enter item name: ");
    getchar();
    fgets(item.name, sizeof(item.name), stdin);
