    if (rows1 != rows2 || columns1 != columns2) {
        printf("Matrix dimensions do not match for subtraction!\n");
        return -1;
    }

    for (int i = 0; i < rows1; i++) {
        for (int j = 0; j < columns1; j++) {
            difference[i][j] = MatrixA[i][j] - MatrixB[i][j];
        }
    }

