    }

    Student *sorted = NULL;
    Student *current = head;

    while (current)
    {
        Student *next = current->next;

        if (!sorted || current->grade < sorted->grade)
        {
            current->next = sorted;
            sorted = current;
        }
        else
        {
            Student *temp = sorted;
            while (temp->next && temp->next->grade <= current->grade)
            {
                temp = temp->next;
            }
            current->next = temp->next;
            temp->next = current;
        }

        current = next;
    }

    head = sorted;
    printf("\nRecords sorted by grade.\n");
    displayStudents();
}

