int validation(int row, int column) {
    if(row > 0 && row <= 10 && column > 0 && column <=10) {
        return 1;
    }
    return 0;
}

void present(int *result, int row, int column){
    for (int i = 0; i < row * column; i++) {
        printf("%d ", result[i]);
        if ((i + 1) % row == 0) {
            printf("\n");
        }
    }
}

void Print_Menu(int a) {
    printf("Matrix Operations Menu:\n");
