                    }
                    break;
                }
                
                while (1) {
                    printf("Enter Grade: ");
                    if (scanf("%f", &grade) != 1 || grade < 0) {
                        printf("Invalid Grade. Enter again.\n");
                        while (getchar() != '\n');
                        continue;
                    }
                    break;
                }
                insertStudent(name, id, grade);
                break;
            case 2:
                displayStudents();
                break;
            case 3:
                sortStudents();
                break;
            case 4:
                printf("Enter ID to search: ");
