    scanf(" %d%d", &rowB, &columnB);

    for(int a = 0; a<rowB; a++) {
        for(int b = 0; b<columnB; b++) {
            printf("Element [%d][%d]: ", a+1, b+1);
            scanf("%d", &B[a][b]);
        }
    }
while(choice!=5)    {
