#define MAXLIST 100

// struct declarations
typedef struct 
{
    char name[100];
    char category[50];
    int quantity;
    float price;
} Grocery_item;

typedef struct
{
    bool in_stock;
    bool outof_stock;
} Availability_status;

typedef struct
{
    Grocery_item item[MAXLIST];
    Availability_status status[MAXLIST];
    int total;
} Grocery_inventory;
Grocery_inventory inventory;

// function prototype declarations
void addItem ();
void displayItems ();
void updatequantity ();
void removeitem ();

int main ()
{
    while (1)
    {
        printf("\nGrocery Inventory Management System\n");
