#include <stdio.h> // Standard library for input/output
#include <stdlib.h> // Library for free() and malloc()
#include <string.h> // Library for string

// Structure for Student 
typedef struct Student { 
    char name[100]; 
    int id; 
    float grade; 
    struct Student* next; 
} Student; 

Student* head = NULL; 

// Function to create a new student node 
Student* createStudent(const char* name, int id, float grade) { 
    Student* newStudent = (Student*)malloc(sizeof(Student));
    if (!newStudent) {
        printf("Memory allocation failed.\n");
        return NULL;
    }
    strcpy(newStudent->name, name);
    newStudent->id = id;
    newStudent->grade = grade;
    newStudent->next = NULL;
    return newStudent;
}

// Function to insert a new student record 
void insertStudent(const char* name, int id, float grade) { 
    Student* newStudent = createStudent(name, id, grade);
    if (!newStudent) return;

    if (!head || head->grade > grade) {
        newStudent->next = head;
        head = newStudent;
    } else {
        Student* current = head;
        while (current->next && current->next->grade <= grade) {
            current = current->next;
        }
        newStudent->next = current->next;
        current->next = newStudent;
    }
    printf("Student record inserted successfully.\n");
}

// Function to display all student records 
void displayStudents() { 
    if (!head) {
        printf("No student records found.\n");
        return;
    }
    Student* current = head;
    while (current) {
        printf("Name: %s, ID: %d, Grade: %.2f\n", current->name, current->id, current->grade);
        current = current->next;
    }
}

// Function to search for a student by ID 
Student* searchStudentByID(int id) { 
    Student* current = head;
    while (current) {
        if (current->id == id) {
            return current;
        }
        current = current->next;
    }
    return NULL;
}

// Function to delete the entire list 
void deleteList() { 
    Student* current = head; 
    Student* next; 

    while (current != NULL) { 
        next = current->next; 
        free(current); 
        current = next; 
    } 
    head = NULL; 
} 

void freeMemory() { 
    deleteList(); 
} 

// Sort function to sort the student records by grade based on an algorithm of your choice (Bubble Sort)
void sortStudents() { 
    if (!head || !head->next) return;

    int swapped;
    Student* ptr1;
    Student* lptr = NULL;

    do {
        swapped = 0;
        ptr1 = head;

        while (ptr1->next != lptr) {
            if (ptr1->grade > ptr1->next->grade) {
                float tempGrade = ptr1->grade;
                int tempID = ptr1->id;
                char tempName[100];

                strcpy(tempName, ptr1->name);
                ptr1->grade = ptr1->next->grade;
                ptr1->id = ptr1->next->id;
                strcpy(ptr1->name, ptr1->next->name);

                ptr1->next->grade = tempGrade;
                ptr1->next->id = tempID;
                strcpy(ptr1->next->name, tempName);

                swapped = 1;
            }
            ptr1 = ptr1->next;
        }
        lptr = ptr1;
