void subMatrices(int matrixA[MAX_SIZE][MAX_SIZE], int matrixB[MAX_SIZE][MAX_SIZE], int rows, int columns);
void transposeMatrix(int matrix[MAX_SIZE][MAX_SIZE], int rows, int columns, char name);

