        printf("Name: %s, ID: %d, Grade: %.2f\n", current->name, current->id, current->grade);
        current = current->next;
    }
}

Student* searchStudentByID(int id) {
    Student* current = head;

    while (current) {
        if (current->id == id) {
            return current;
        }
        current = current->next;
    }

    return NULL;
}

void deleteList() {
    Student* current = head;
    Student* next;

    while (current) {
        next = current->next;
        free(current);
        current = next;
    }
    
    head = NULL;
}

void freeMemory() {
    deleteList();
}

void sortStudents() {
    if (!head || !head->next) {
        return; 
    }

    Student* i;
    Student* j;
    for (i = head; i != NULL; i = i->next) {
        for (j = i->next; j != NULL; j = j->next) {
            if (i->grade > j->grade) {
                // Swap data
                char tempName[100];
                int tempID;
                float tempGrade;

                strcpy(tempName, i->name);
                tempID = i->id;
                tempGrade = i->grade;

                strcpy(i->name, j->name);
                i->id = j->id;
                i->grade = j->grade;

                strcpy(j->name, tempName);
                j->id = tempID;
                j->grade = tempGrade;
            }
        }
    }
}

