#include <stdio.h> 
#include <stdlib.h> 
#include <string.h> 

// Structure for Student 
typedef struct Student { 
    char name[100]; 
    int id; 
    float grade; 
    struct Student* next; 
} Student; 

Student* head = NULL; 

// Function to create a new student node 
Student* createStudent(const char* name, int id, float grade) { 
    Student* newStudent = (Student*)malloc(sizeof(Student));
    if(newStudent == NULL) {
    	printf("Memory not allocated.\n");  
    }

    strcpy(newStudent->name, name);
    newStudent->id = id;
    newStudent->grade = grade;
    newStudent->next = NULL;
    return newStudent;

} 

// Function to insert a new student record 
void insertStudent(const char* name, int id, float grade) { 
    Student* newStudent = createStudent(name, id, grade);
    newStudent->next = head;
    head = newStudent;
} 

// Function to display all student records 
void displayStudents() { 
    if(head == NULL) {
        printf("No student records available.\n");
        return;
    }

    Student* current = head;
    while (current) {
        printf("Name: %s, ID: %d, Grade: %.2f\n", current->name, current->id, current->grade);
        current = current->next;
    }
} 

// Function to search for a student by ID 
Student* searchStudentByID(int id) { 
    Student* current = head;
    while (current) {
        if (current->id == id) {
            return current;
        }
        current = current->next;
    }
    return NULL;
} 

// Function to delete the entire list 
void deleteList() { 
    Student* current = head; 
    Student* next; 

    while (current != NULL) { 
        next = current->next; 
        free(current); 
        current = next; 
    } 
    head = NULL; 
} 



void freeMemory() { 
    deleteList(); 
} 

// Sort function to sort the student records by Bubble Sort 
/**Reason: If the list is already sorted or nearly sorted, 
it can terminate early if no swaps are made during a pass**/
void sortStudents() { 
    if (head == NULL || head->next == NULL) {
        printf("No need to sort. The list has zero or one student.\n");
        return;
    }

    Student* current;
    Student* prev = NULL;
    int swapped;

    do {
        swapped = 0;
        current = head;

        while (current->next != prev) {
            if (current->grade > current->next->grade) {
                // Swap the contents of current and next node
                char temp[100];
                int tempID;
                float tempGrade;

                strcpy(temp, current->name);
                tempID = current->id;
                tempGrade = current->grade;

                strcpy(current->name, current->next->name);
                current->id = current->next->id;
                current->grade = current->next->grade;

                strcpy(current->next->name, temp);
                current->next->id = tempID;
                current->next->grade = tempGrade;

                swapped = 1;
            }
            current = current->next;
        }
        prev = current; 
    } while (swapped);

    printf("Records sorted by grade using Bubble Sort.\n");
} 

