#include <stdio.h>
#include <string.h>
#include <stdbool.h>

#define MAX_ITEMS 100
#define MAX_NAME_LENGTH 100
#define MAX_CATEGORY_LENGTH 50

// Structure for Grocery Item
struct GroceryItem {
    char name[MAX_NAME_LENGTH];
    char category[MAX_CATEGORY_LENGTH];
    int quantity;
    float price;
};

// Enum for availability status
typedef enum {
    IN_STOCK,
    OUT_OF_STOCK
} AvailabilityStatus;

// Structure for Inventory
struct GroceryInventory {
    struct GroceryItem items[MAX_ITEMS];
    AvailabilityStatus status[MAX_ITEMS];
    int itemCount;
};

void addGroceryItem(struct GroceryInventory *inventory);
void listGroceryItems(struct GroceryInventory *inventory);
void updateQuantity(struct GroceryInventory *inventory);
void removeGroceryItem(struct GroceryInventory *inventory);

// Function to list all grocery items with their availability
void listGroceryItems(struct GroceryInventory *inventory) {
    if (inventory->itemCount == 0) {
        printf("No items in the inventory.\n");
        return;
    }

    printf("\n--- Grocery Inventory ---\n");
    for (int i = 0; i < inventory->itemCount; i++) {
        printf("Item %d: %s\n", i + 1, inventory->items[i].name);
        printf("Category: %s\n", inventory->items[i].category);
        printf("Quantity: %d\n", inventory->items[i].quantity);
        printf("Price: %.2f\n", inventory->items[i].price);
        printf("Availability: %s\n", inventory->status[i] == IN_STOCK ? "In Stock" : "Out of Stock");
        printf("\n");
    }
}

// Function to update the quantity of a grocery item
void updateQuantity(struct GroceryInventory *inventory) {
    if (inventory->itemCount == 0) {
        printf("No items in the inventory to update.\n");
        return;
    }

    int itemIndex;
    printf("Enter the item number to update quantity: ");
    scanf("%d", &itemIndex);

    if (itemIndex < 1 || itemIndex > inventory->itemCount) {
        printf("Invalid item number.\n");
        return;
    }

    itemIndex--; // Adjust for 0-based index
    printf("Current quantity: %d\n", inventory->items[itemIndex].quantity);
    printf("Enter new quantity: ");
    scanf("%d", &inventory->items[itemIndex].quantity);

    // Update availability status based on new quantity
    if (inventory->items[itemIndex].quantity > 0) {
        inventory->status[itemIndex] = IN_STOCK;
    } else {
        inventory->status[itemIndex] = OUT_OF_STOCK;
    }

    printf("Quantity updated successfully!\n");
}

// Function to remove a grocery item from the inventory
void removeGroceryItem(struct GroceryInventory *inventory) {
    if (inventory->itemCount == 0) {
        printf("No items in the inventory to remove.\n");
        return;
    }

    int itemIndex;
    printf("Enter the item number to remove: ");
    scanf("%d", &itemIndex);

    if (itemIndex < 1 || itemIndex > inventory->itemCount) {
        printf("Invalid item number.\n");
        return;
    }

    itemIndex--; // Adjust for 0-based index
    for (int i = itemIndex; i < inventory->itemCount - 1; i++) {
        inventory->items[i] = inventory->items[i + 1];
        inventory->status[i] = inventory->status[i + 1];
    }

    inventory->itemCount--;
    printf("Item removed successfully!\n");
}

int main() {
    struct GroceryInventory inventory = {{0}, {OUT_OF_STOCK}, 0};  // Initialize inventory

    int choice;
    do {
         printf("\n-- Grocery Inventory Menu --\n");
        printf("1. Add grocery item\n");
        printf("2. List all grocery items\n");
        printf("3. Update quantity\n");
        printf("4. Remove grocery item\n");
        printf("5. Exit\n");
        printf("Enter your choice: ");
        scanf("%d", &choice);

        switch(choice) {
            case 1:
                addGroceryItem(&inventory);
                break;
            case 2:
                listGroceryItems(&inventory);
                break;
            case 3:
                updateQuantity(&inventory);
                break;
            case 4:
                removeGroceryItem(&inventory);
                break;
            case 5:
                printf("Exiting program.\n");
                break;
            default:
                printf("Invalid choice. Please try again.\n");
        }
    } while (choice != 5);

    return 0;
}

// Function to add a grocery item to the inventory
void addGroceryItem(struct GroceryInventory *inventory) {
    if (inventory->itemCount >= MAX_ITEMS) {
        printf("Inventory is full, cannot add more items.\n");
        return;
    }

    struct GroceryItem newItem;
    printf("Enter item name: ");
    getchar();  // To consume the newline character left by scanf
    fgets(newItem.name, MAX_NAME_LENGTH, stdin);
    newItem.name[strcspn(newItem.name, "\n")] = 0;  // Remove trailing newline

    printf("Enter category: ");
    fgets(newItem.category, MAX_CATEGORY_LENGTH, stdin);
    newItem.category[strcspn(newItem.category, "\n")] = 0;

    printf("Enter quantity: ");
    scanf("%d", &newItem.quantity);

    printf("Enter price: ");
    scanf("%f", &newItem.price);

    // Set availability status based on quantity
    if (newItem.quantity > 0) {
        inventory->status[inventory->itemCount] = IN_STOCK;
    } else {
        inventory->status[inventory->itemCount] = OUT_OF_STOCK;
    }

    inventory->items[inventory->itemCount] = newItem;
    inventory->itemCount++;
    printf("Grocery item added successfully!\n");
}
/* --- Q3.c --- */
#include <stdio.h>
#include <stdlib.h>
#include <string.h>

typedef struct Student {
    char name[100];
    int id;
    float grade;
    struct Student* next;
} Student;

// Head pointer to the linked list
Student* head = NULL;

// Function prototypes
Student* createStudent(const char* name, int id, float grade);
void insertStudent(const char* name, int id, float grade);
void displayStudents();
Student* searchStudentByID(int id);
void deleteList();
void freeMemory();
void sortStudents();

// Function to create a new student node
Student* createStudent(const char* name, int id, float grade) {
    Student* newStudent = (Student*)malloc(sizeof(Student));
    if (!newStudent) {
        printf("Memory allocation failed.\n");
        exit(1);
    }

    strcpy(newStudent->name, name);
    newStudent->id = id;
    newStudent->grade = grade;
    newStudent->next = NULL;

    return newStudent;
}

// Function to insert a new student record into the linked list
void insertStudent(const char* name, int id, float grade) {
    Student* newStudent = createStudent(name, id, grade);
    newStudent->next = head;
    head = newStudent;
    printf("Student record inserted successfully!\n");
}

// Function to display all student records
void displayStudents() {
    if (head == NULL) {
        printf("No student records available.\n");
        return;
    }

    Student* current = head;
    printf("\n-- Student Records --\n");
    while (current != NULL) {
        printf("Name: %s, ID: %d, Grade: %.2f\n", current->name, current->id, current->grade);
        current = current->next;
    }
}

// Function to search for a student by ID
Student* searchStudentByID(int id) {
    Student* current = head;
    while (current != NULL) {
        if (current->id == id) {
            return current;
        }
        current = current->next;
    }
    return NULL;  // Student not found
}

// Function to delete the entire list
void deleteList() {
    Student* current = head;
    Student* temp;

    while (current != NULL) {
        temp = current;
        current = current->next;
        free(temp);
    }

    head = NULL;
}

// Function to free the memory when the program exits
void freeMemory() {
    deleteList();
}

// Function to sort the student records by grade 
void sortStudents() {
    if (head == NULL || head->next == NULL) {
        return;  // List is empty or has only one element
    }

    Student* current;
    Student* next;
    float tempGrade;
    int tempID;
    char tempName[100];

    // Bubble Sort
    for (current = head; current != NULL; current = current->next) {
        for (next = current->next; next != NULL; next = next->next) {
            if (current->grade > next->grade) {
                // Swap the student data
                strcpy(tempName, current->name);
                strcpy(current->name, next->name);
                strcpy(next->name, tempName);

                tempID = current->id;
                current->id = next->id;
                next->id = tempID;

                tempGrade = current->grade;
                current->grade = next->grade;
                next->grade = tempGrade;
            }
        }
    }
}

// Main function
int main() {
    int choice, id;
    char name[100];
    float grade;
    Student* student;

    do {
        printf("\n-- Student Records Menu --\n");
        printf("1. Insert Student Record\n");
        printf("2. Display Student Records\n");
        printf("3. Sort Records\n");
        printf("4. Search Record by ID\n");
        printf("5. Delete List\n");
        printf("6. Exit\n");
        printf("Enter your choice: ");
        scanf("%d", &choice);

        switch (choice) {
            case 1:
                printf("Enter student name: ");
                getchar();  // To consume the newline left by scanf
                fgets(name, sizeof(name), stdin);
                name[strcspn(name, "\n")] = '\0';  

                printf("Enter student ID: ");
                scanf("%d", &id);

                printf("Enter student grade: ");
                scanf("%f", &grade);

                insertStudent(name, id, grade);
                break;

            case 2:
                displayStudents();
                break;

            case 3:
                sortStudents();
                printf("Student records sorted by grade using Bubble Sort.\n");
                break;

            case 4:
                printf("Enter student ID to search: ");
                scanf("%d", &id);

                student = searchStudentByID(id);
                if (student != NULL) {
                    printf("Student Found: %s (ID: %d, Grade: %.2f)\n", student->name, student->id, student->grade);
                } else {
                    printf("Student with ID %d not found.\n", id);
                }
                break;

            case 5:
                deleteList();
                printf("All student records deleted.\n");
                break;

            case 6:
                freeMemory();
                printf("Exiting program.\n");
                break;

            default:
                printf("Invalid choice. Please try again.\n");
        }
    } while (choice != 6);

    return 0;
}

/* --- Q1.c --- */
#include <stdio.h>

// Function headers for the initializing matrices and 3 operations to be performed on matrices.
void validate(int *row, int *col);
void populateMatrix(int rows, int cols, int arr[rows][cols]);
void Addition(int row1, int row2, int col1, int col2, int matA[row1][col1], int matB[row2][col2]);
void Subtraction(int row1, int row2, int col1, int col2, int matA[row1][col1], int matB[row2][col2]);
void Transpose(int row, int col, int arr[row][col]);

// Function to validate range of matrix dimensions, ensuring it is within 1x1 to 10x10.
// Variables row and col are passed as pointers to directly change the values via the function.
void validate(int *row, int *col){
    while(*row<1 || *row>10){
        printf("Dimension for row is out of range. Please re-enter row:\n");
        scanf("%d", row);
    }
    while(*col<1 || *col>10){
        printf("Dimension for column is out of range. Please re-enter column:\n");
        scanf("%d", col);
    }
}

// Function to populate matrix with values provided by user.
// Validates if any non integer values are entered. If yes, prompt for re-input.
void populateMatrix(int rows, int cols, int arr[rows][cols]){
    int element;

    for(int x=0;x<rows;x++){
        for(int y=0;y<cols;y++){
            while (1){ // Infinite loop to accomodate validation
                printf("Element [%d][%d]: ", x + 1, y + 1);
                if (scanf("%d", &element) == 1) { // Check if input is a valid integer
                    arr[x][y] = element; 
                    break; // Exit the loop if it is a valid integer for the current element
                } else {
                    printf("Invalid input. Please enter a valid integer.\n");
                    while (getchar() != '\n'); // Need to clear input buffer to prevent infinite loop of error in program behaviour
                }
            }
        }
    }
    printf("\n");
}

// Function to add matrix A and B. By laws of matrices - In order for addition to be performed both matrix must have same dimensions.
void Addition(int row1, int row2, int col1, int col2, int matA[row1][col1], int matB[row2][col2]){

    if(row1 != row2 || col1 != col2){
        // Unable to perform addition if different dimensions. Output error message and skip addition process.
        printf("Both matrices do not have the same dimensions. Unable to perform matrix addition.\n");
    } else {
        printf("\nResult of matrix addition:");
        for(int x=0;x<row1;x++){
            printf("\n");
            for(int y=0;y<col1;y++){
                printf("%d ",matA[x][y] + matB[x][y]); // Prints final output while looping
            }
        }
    }
    printf("\n");
}

// Function to subtract matrix B from A. By laws of matrices - In order for subtraction to be performed both matrix must have same dimensions.
void Subtraction(int row1, int row2, int col1, int col2, int matA[row1][col1], int matB[row2][col2]){
    
    if(row1 != row2 || col1 != col2){
        // Same as addition function
        printf("Both matrices do not have the same dimensions. Unable to perform matrix subtraction.\n");
    } else {
        printf("\nResult of matrix subtraction:");
        for(int x=0;x<row1;x++){
            printf("\n");
            for(int y=0;y<col1;y++){
                printf("%d ",matA[x][y] - matB[x][y]);
            }
        }
    }
    printf("\n");
}

// Function to transpose matrix
void Transpose(int row, int col, int arr[row][col]){
    
    // Transposed matrix will be stored in here. Dimensions are flipped to accomodate rules of transposition
    int Transpose[col][row]; 
    
    // Flipping matrix according to diagonal
    for (int i = 0; i < row; ++i){
        for (int j = 0; j < col; ++j){
            Transpose[j][i] = arr[i][j];
        }
    }

    // Print final matrix
    printf("\nTranspose of the matrix:\n");
    for (int i = 0; i < col; ++i){
        for (int j = 0; j < row; ++j){
            printf("%d  ", Transpose[i][j]);
            if (j == row - 1)
            printf("\n");
        }
    }
    printf("\n");
}

// Main function to start the program
int main(void){
    int row1, row2, col1, col2;

    // Prompts user for matrix A and B dimensions, calls validate function to validate input
    printf("Enter the dimensions of matrix A: (Min - 1x1, Max - 10x10)\n");
    scanf("%d%d", &row1,&col1);
    validate(&row1,&col1);
    
    printf("Enter the dimensions of matrix B: (Min - 1x1, Max - 10x10)\n");
    scanf("%d%d", &row2,&col2);
    validate(&row2,&col2);

    // Initialize matrices after getting user input
    int matA[row1][col1], matB[row2][col2];

    // Calls populate function to populate matrices
    printf("Enter elements for Matrix A (%dx%d):\n", row1,col1);
    populateMatrix(row1,col1,matA);
    printf("\n");
    printf("Enter elements for Matrix B (%dx%d):\n", row2, col2);
    populateMatrix(row2,col2,matB);

    printf("Matrix Operations Menu:\n");
    printf("1. Add Matrices\n");
    printf("2. Subtract Matrices\n");
    printf("3. Transpose Matrix A\n");
    printf("4. Transpose Matrix B\n");
    printf("5. Exit\n");
    
    // Infinite loop to accomodate multiple operations from user. Only ends when user decides to terminate.
    int choice;
    while(1){
        printf("\nEnter your choice (5 to exit): ");
        if (scanf("%d", &choice) == 1) { // Check if input is a valid integer
            // Condition to end infinite loop and terminate program.
            if (choice == 5) { 
                printf("Program terminated by user.");
                break;
            }
            // Calls respective functions base on user operations.
            switch (choice) {
                case 1:
                    Addition(row1, row2, col1, col2, matA, matB);
                    break;
                case 2:
                    Subtraction(row1, row2, col1, col2, matA, matB);
                    break;  
                case 3:
                    Transpose(row1, col1, matA);
                    break;
                case 4:
                    Transpose(row2, col2, matB);
                    break;
                // To check if user input integer that is not 1-5. Shows error message and prompts for re-input.
                default:
                    printf("Invalid choice. Please enter a valid option (1-5).\n");
            }
        } else { // If input is not a valid integer, shows error message and prompts for re-input
            printf("Invalid input. Please enter a valid integer.\n");
            printf("\n");
            while (getchar() != '\n'); // Need to clear input buffer to prevent infinite loop of error in program behaviour
        }
    }
    
    return 0;
}
