    float price; // 
    bool inStock; // 
} GroceryItem;

// 
GroceryItem inventory[MAX_ITEMS];
int itemCount = 0; // 

// 
void addGroceryItem(); // 
void listGroceryItems(); // 
void updateQuantity(); // 
void removeGroceryItem(); // 

int main2() //main
{
    int choice;
    do
    {
        // 
