    if (head == NULL || head->next == NULL) return;

    int swapped;
    Student* ptr;
    Student* lptr = NULL;

    do {
        swapped = 0;
        ptr = head;

        while (ptr->next != lptr) {
            if (ptr->grade > ptr->next->grade) {
                char tempName[100];
                int tempID;
                float tempGrade;

                strcpy(tempName, ptr->name);
                strcpy(ptr->name, ptr->next->name);
                strcpy(ptr->next->name, tempName);

                tempID = ptr->id;
                ptr->id = ptr->next->id;
                ptr->next->id = tempID;

                tempGrade = ptr->grade;
                ptr->grade = ptr->next->grade;
                ptr->next->grade = tempGrade;

                swapped = 1;
            }
            ptr = ptr->next;
        }
        lptr = ptr;
    } while (swapped);
}

