void subMatrix(int a[MAX_SIZE][MAX_SIZE],int b[MAX_SIZE][MAX_SIZE],int c[MAX_SIZE][MAX_SIZE] ,int rows, int cols);
void tranposeMatrix(int a[MAX_SIZE][MAX_SIZE], int b[MAX_SIZE][MAX_SIZE], int rows, int cols );


int main(void) {
