        getchar();

        switch (choice)
        {
        case 1:
            AddGrocery(&inventory);
            break;

        case 2:
            listGrocery(&inventory);
            break;

        case 3:
            updateGrocery(&inventory);
            break;

        case 4:
            removeGrocery(&inventory);
            break;

        case 5:
            printf("Exiting...\n");
            break;

        default:
            printf("Invalid choice. Please try again. \n");
        }
    } while (choice != 5);

    return 0;
