#include <stdio.h>
#include <stdlib.h>
#include <string.h>

// Structure for Student
typedef struct Student {
    char name[100];
    int id;
    float grade;
    struct Student* next;
} Student;

Student* head = NULL;

// Function to create a new student node
Student* createStudent(const char* name, int id, float grade) {
    Student* addStudent = (Student*)malloc(sizeof(Student));
    if (!addStudent) {
        printf("Memory allocation failed.\n");
        exit(EXIT_FAILURE);
    }
    strcpy(addStudent->name, name);
    addStudent->id = id;
    addStudent->grade = grade;
    addStudent->next = NULL;
    return addStudent;
}

// Function to insert a new student record
void insertStudent(const char* name, int id, float grade) {
    Student* addStudent = createStudent(name, id, grade);
    if (head == NULL) {
        head = addStudent;
    } else {
        Student* temp = head;
        while (temp->next != NULL ) {
            temp = temp->next;
        }
        temp->next = addStudent;
    }
}

// Function to display all student records
void displayStudents() {
    if (head == NULL) {
        printf("No student records available. \n");
        return;
    }
    Student* temp = head;
    while (temp != NULL) {
        printf("Name: %s ID: %d Grade: %.2f \n", temp->name, temp->id, temp->grade);
        temp = temp->next;
    }
}

// Function to search for a student by ID
Student* searchStudentByID(int id) {
    Student* temp = head;
    while (temp != NULL) {
        if (temp->id == id) {
            return temp;
        }
        temp = temp->next;
    }
    return NULL;
}

// Function to delete the entire list
void deleteList() {
    Student* current = head;
    Student* next;
    while (current != NULL) {
        next = current->next;
        free(current);
        current = next;
    }
    head = NULL;
} 

void freeMemory() {
    deleteList();
}

// Sort function to sort the student records by grade based on an
// algorithm of your choice
void sortStudents() {
    if (head == NULL || head->next == NULL) {
        printf("Not enough record to sort.\n");
        return;
    }

    int swapped;
    Student *current, *prev = NULL;

    do {
        swapped = 0;
        current = head;

        // Bubble sorting
        while (current->next != prev) {
            if (current->grade > current->next->grade) {
                // Arranging student 
                char tempName[100];
                int tempID;
                float tempGrade;

                strcpy(tempName, current->name);
                tempID =  current->id;
                tempGrade = current->grade;

                strcpy(current->name, current->next->name);
                current->id = current->next->id;
                current->grade = current->next->grade;

                strcpy(current->next->name, tempName);
                current->next->id =  tempID;
                current->next->grade = tempGrade;

                swapped = 1;
            }
            current = current->next;
        }
        prev = current;
    } while (swapped);

    printf("Student records sorted by grade.\n");
}

