        // Clear input buffer
        while (getchar() != '\n');

        // Perform the selected operation
        switch (choice) {
            case 1:
                AddGroceryItems(&inventory);
                break;
            case 2:
                ListGroceryItems(&inventory);
                break;
            case 3:
                UpdateQuantity(&inventory);
                break;
            case 4:
                RemoveGroceryItems(&inventory);
                break;
            case 5:
                printf("Exiting...\n");
                return 0;
            default:
                printf("Invalid choice! Please try again!\n");
                break;
        }
    }

    return 0;
}

