#include <stdio.h>
#include <stdlib.h>
#include <string.h>

// Structure for Student
typedef struct Student {
    char name[100];
    int id;
    float grade;
    struct Student* next;
} Student;

Student* head = NULL;  // Head of the linked list

// Function to create a new student node
Student* createStudent(const char* name, int id, float grade) {
    Student* newStudent = (Student*)malloc(sizeof(Student));
    if (newStudent == NULL) {
        printf("Memory allocation failed!\n");
        return NULL;
    }
    strcpy(newStudent->name, name);  // Copy the name into the student record
    newStudent->id = id;
    newStudent->grade = grade;
    newStudent->next = NULL;
    return newStudent;
}

// Function to insert a new student record
void insertStudent(const char* name, int id, float grade) {
    Student* newStudent = createStudent(name, id, grade);
    if (newStudent == NULL) return;

    // Insert at the beginning of the list
    newStudent->next = head;
    head = newStudent;
}

// Function to display all student records
void displayStudents() {
    if (head == NULL) {
        printf("No student records available.\n");
        return;
    }

    Student* current = head;
    printf("\n");
    while (current != NULL) {
        printf("Name: %s, ID: %d, Grade: %.2f\n", current->name, current->id, current->grade);
        current = current->next;
    }
}

// Function to search for a student by ID
Student* searchStudentByID(int id) {
    Student* current = head;
    while (current != NULL) {
        if (current->id == id) {
            return current;  // Return student with the matching ID
        }
        current = current->next;
    }
    return NULL;  // Return NULL if no student with the given ID is found
}

// Function to delete the entire list
void deleteList() {
    Student* current = head;
    Student* next;
    while (current != NULL) {
        next = current->next;
        free(current);  // Free memory of the current student node
        current = next;
    }
    head = NULL;  // Set head to NULL after the list is deleted
}

// Utility function to free memory
void freeMemory() {
    deleteList();
}

// Bubble Sort to sort the student records by grade in ascending order
void sortStudents() {
    if (head == NULL || head->next == NULL) {
        return;  // List is empty or contains only one element
    }

    Student* current;
    Student* next;
    int swapped;

    do {
        swapped = 0;
        current = head;
        while (current != NULL && current->next != NULL) {
            next = current->next;
            if (current->grade > next->grade) {
                // Swap grades
                float tempGrade = current->grade;
                current->grade = next->grade;
                next->grade = tempGrade;

                // Swap names
                char tempName[100];
                strcpy(tempName, current->name);  // Potential risk: No validation for string size
                strcpy(current->name, next->name);
                strcpy(next->name, tempName);

                // Swap IDs
                int tempID = current->id;
                current->id = next->id;
                next->id = tempID;

                swapped = 1;
            }
            current = current->next;
        }
    } while (swapped);
