    scanf("%d %d", &rowsA, &colsA);
    if (rowsA > MAX || colsA > MAX || rowsA <= 0 || colsA <= 0) {
        printf("Invalid dimensions! Exiting program.\n");
        return 1;
    }
    inputMatrix(rowsA, colsA, A, 'A');

    // Dimensions and contents of input matrix B
